Running.sender:
{
  for (let i = 0; i < 50; ++i) {
    let count = mailbox.count("smart_parking_tx");
    if (count > 0) {
      let mailbox_message = mailbox.pop("smart_parking_tx");
      let smart_parking = mailbox_message;

      if (!state.smart_parkings[smart_parking.id]) {
        console.log("New parking spot added", smart_parking);
      }
      state.smart_parkings[smart_parking.id] = smart_parking;
    }

    let countEV = mailbox.count("ev_car_"+i+"_parking");
    if (countEV > 0) {
      let ev_mailbox_message = mailbox.pop("ev_car_"+i+"_parking");

      let nearestParking = null;
      let minDistance = 1000000;
      for (let parkingID in state.smart_parkings) {
        let distance = geolib.getDistance(
          { latitude: ev_mailbox_message.current_latitude, longitude: ev_mailbox_message.current_longitude },
          {
            latitude: state.smart_parkings[parkingID].latitude,
            longitude: state.smart_parkings[parkingID].longitude,
          }
        );
        if (distance < minDistance) {
          minDistance = distance;
          nearestParking = state.smart_parkings[parkingID];
        }
      }

      if (nearestParking) {
        mailbox.post("ev_car_"+i, nearestParking);
      }
    }


  }

}
