Running.sender:
{
  if (chance.bool({ likelihood: 10 })) {
    state.last_coffe = {
      temperature: chance.pickone([60, 70, 80]),
      bean_amount: chance.pickone([1, 2, 3]),
      fill_quantity: chance.pickone([1, 2, 3]),
    }
    state.bean_level = state.bean_level - state.last_coffe.bean_amount
    state.water_level = state.water_level - state.last_coffe.fill_quantity
    state.usage_count = state.usage_count + 1
  }
  var message = {
    timestamp: moment.now(),
    last_coffe: state.last_coffe,
    usage_count: state.usage_count,
    bean_level: state.bean_level,
    water_level: state.water_level
  };

  return JSON.stringify(message, null, 2);
}
