Init.sender:
{
  state.usage_count = 0
  state.bean_level = 100
  state.water_level = 100
  state.milk_level = 100
  state.chocolate_level = 100

  state.grind_coffee = 3
  state.extract_coffee = 2
  state.boil_milk = 2
  state.boil_water = 2
  state.froth_milk = 2

  state.options = [
    {
      espresso: true,
      milk: true,
      name: 'Latte',
    },
    {
      espresso: true,
      milk: true,
      foam: true,
      name: 'Cappucino',
    },
    {
      espresso: true,
      water: true,
      name: 'Americano',
    },
    {
      espresso: true,
      foam: true,
      name: 'Macchiato',
    },
    {
      espresso: true,
      milk: true,
      chocolate: true,
      name: 'Mocha',
    },
  ];
}

Start.sender:
{
  var now = dayjs().format()

  state.current_brew = chance.integer({ min: 0, max: 4 })

  console.log("Starting brew: " + state.options[state.current_brew].name)
  state.usage_count = state.usage_count + 1

  var message = {
    timestamp: now,
    current_brew: state.current_brew,
    usage_count: state.usage_count,
    bean_level: state.bean_level,
    water_level: state.water_level,
    milk_level: state.milk_level,
    chocolate_level: state.chocolate_level,
  };

  next('Espresso')

  return JSON.stringify(message, null, 2);



  if (chance.bool({ likelihood: 10 })) {
    state.last_coffe = {
      temperature: chance.pickone([60, 70, 80]),
      bean_amount: chance.pickone([1, 2, 3]),
      fill_quantity: chance.pickone([1, 2, 3]),
    }
    state.bean_level = state.bean_level - state.last_coffe.bean_amount
    state.water_level = state.water_level - state.last_coffe.fill_quantity
  }

}

Espresso.sender:
{ 
  var now = dayjs().format()

  while (state.refilling_coffee) {
    console.log("Refilling Beans")
    if (state.bean_level < 80) {
      state.bean_level += 20
    }
    else {
      state.bean_level = 100
      state.refilling_coffee = false
    }
  }

  if (state.bean_level <= 20) {
    state.refilling_coffee = true
  }
  else {
    if (--state.grind_coffee <= 0) {
      console.log("Coffee beans grinded")

      if (--state.extract_coffee <= 0) {
        console.log("Espresso Made")
        state.grind_coffee = 2
        state.extract_coffee = 2

        if (state.options[state.current_brew].milk) {
          next('Milk')
        }
        else if (state.options[state.current_brew].water) {
          next('Water')
        }
        else if (state.options[state.current_brew].foam) {
          next('Foam')
        }
      }
    }
  }

  var message = {
    timestamp: now,
    current_brew: state.current_brew,
    usage_count: state.usage_count,
    bean_level: state.bean_level,
    water_level: state.water_level,
    milk_level: state.milk_level,
    chocolate_level: state.chocolate_level,
  };

  return JSON.stringify(message, null, 2);
}

Milk.sender:
{
  var now = dayjs().format()

  while (state.refilling_milk) {
    console.log("Refilling Milk")
    if (state.milk_level < 80) {
      state.milk_level += 20
    }
    else {
      state.milk_level = 100
      state.refilling_milk = false
    }
  }

  if (state.milk_level <= 20) {
    state.refilling_milk = true
  }
  else {
    if (--state.boil_milk <= 0) {
      console.log("Milk boiled")
      state.boil_milk = 2

      if (state.options[state.current_brew].foam) {
        next('Foam')
      }
      else if (state.options[state.current_brew].chocolate) {
        next('Chocolate')
      }
      else {
        console.log(state.options[state.current_brew].name+" is complete")
        next('Start')
      }
    }
  }

  var message = {
    timestamp: now,
    current_brew: state.current_brew,
    usage_count: state.usage_count,
    bean_level: state.bean_level,
    water_level: state.water_level,
    milk_level: state.milk_level,
    chocolate_level: state.chocolate_level,
  };

  return JSON.stringify(message, null, 2);
}

Water.sender:
{
  var now = dayjs().format()

  while (state.refilling_water) {
    console.log("Refilling Water")
    if (state.water_level < 80) {
      state.water_level += 20
    }
    else {
      state.water_level = 100
      state.refilling_water = false
    }
  }

  if (state.water_level <= 20) {
    state.refilling_water = true
  }
  else {
    if (--state.boil_water <= 0) {
      console.log("Water boiled")
      state.boil_water = 2

      console.log(state.options[state.current_brew].name + " is complete")
      next('Start')
    }
  }

  var message = {
    timestamp: now,
    current_brew: state.current_brew,
    usage_count: state.usage_count,
    bean_level: state.bean_level,
    water_level: state.water_level,
    milk_level: state.milk_level,
    chocolate_level: state.chocolate_level,
  };

  return JSON.stringify(message, null, 2);
}

