Init.sender:
{
  state.API_Key = glob.get('API_Key')
  state.smart_city = glob.get('Enable_Smart_City')

  state.status = "Idle"

  state.id = chance.integer({ min: 1000, max: 9999 });
  state.license = 'ZH ' + chance.string({ length: 6, pool: '0123456789' });

  state.make = "Porsche";
  state.model = "Taycan";

  state.latitude = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
  state.longitude = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });

  state.battery_threshold = chance.integer({ min: 10, max: 30 });
  state.battery_capacity = 84;
  state.battery_level = 80;
  state.max_range = 39000;  //390000
  state.available_range = 39000;
  state.avg_usage = 20;
  state.connector_type = "CCS";

  state.dest_latitude = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
  state.dest_longitude = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });
  state.charging = false;

  state.charger = 0;
  state.charge_lat = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
  state.charge_long = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });

  state.search_parking = false
  state.parking_latitude = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
  state.parking_longitude = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });

  state.distance = 0;

  state.start_point = state.latitude + "," + state.longitude;
  next('Driving_Dest')
}

Driving_Dest.sender:
{

  var message = {
    device: "ev_car",
    id: client(),
    license: state.license,
    make: state.make,
    model: state.model,
    battery_capacity: state.battery_capacity,
    battery_level: state.battery_level,
    available_range: state.available_range,
    connector_type: state.connector_type,
    dest_latitude: state.dest_latitude,
    dest_longitude: state.dest_longitude,
    current_latitude: state.latitude,
    current_longitude: state.longitude,
    charging: state.charging,
    status: "Driving to Destination",
  };

  for (let i = 0; i < 50; ++i) {
    let count = mailbox.count("ev_car_"+client()+"_rx");
    if (count > 0) {
      let mailbox_message = mailbox.pop("ev_car_" + client() + "_rx");
      let smart_parking = mailbox_message;
      state.search_parking = false
      state.parking_latitude = state.dest_latitude = smart_parking.latitude
      state.parking_longitude = state.dest_longitude = smart_parking.longitude
      console.log("Parking found driving to parking spot")

      state.current_location = "" + state.latitude + "," + state.longitude;
      state.parking_location = "" + state.parking_latitude + "," + state.parking_longitude;

      next("Driving_Parking")
    }
  }

  state.end_point = state.dest_latitude + "," + state.dest_longitude;

  state.location = drive({ start: state.start_point, end: state.end_point, key: state.API_Key  });
  console.log(state.location)

  var change = geolib.getDistance(
    { latitude: state.latitude, longitude: state.longitude },
    { latitude: state.location.latitude, longitude: state.location.longitude }
  );
  state.distance = state.distance + change;
  state.available_range = state.available_range - change;

  state.battery_level = Math.ceil(
    (state.available_range / state.max_range) * 100
  );
  state.latitude = state.location.latitude;
  state.longitude = state.location.longitude;

  if (state.location.finished) {
    console.log(
      "We have reached the current destination, Going to the nearest parking."
    );
    state.search_parking = true
  }

  if (state.search_parking == true || state.battery_level < state.battery_threshold) {
    console.log(
      "Currently searching for the nearest parking."
    );
    mailbox.post("ev_car_"+client()+"_parking", message);
  }

  // if (state.battery_level < state.battery_threshold) {
  //   console.log("Battery is low, Driving to charger")
  //   state.current_location = "" + state.location.latitude + "," + state.location.longitude;

  //   state.charge_lat = state.dest_latitude = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
  //   state.charge_long = state.dest_longitude = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });
  //   state.charger_location = "" + state.charge_lat + "," + state.charge_long;

  //   next("Driving_Charger")
  // }

  if (state.smart_city == true) {
    mailbox.post('ev_car_' + client() +"_tx", message)
    return JSON.stringify(message, null, 2);

  }
  else {
    return JSON.stringify(message, null, 2);
  }
}

