Day.sender:
{
  var sunlight = chance.integer({ min: 1, max: 100000 })
  if (sunlight > 50000 && state.moisture > 2)
    state.moisture = state.moisture - 2
  else if (sunlight > 10000 && state.moisture > 0)
    state.moisture = state.moisture - 1
  state.air_temperature = state.air_temperature + chance.floating({ min: -0.1, max: 0.1 })
  state.soil_temperature = state.soil_temperature + (state.air_temperature - state.soil_temperature) / 10
  if (state.moisture < 20) {
    water_pump = "on"
    state.water_tank_level = state.water_tank_level - 1
    state.moisture = state.moisture + 40
  }
  else
    water_pump = "off"
  var message = {
    moisture: state.moisture,
    air_temperature: state.air_temperature,
    soil_temperature: state.soil_temperature,
    sunlight: sunlight,
    water_pump: water_pump,
    water_tank_level: state.water_tank_level,
  }

  return JSON.stringify(message, null, 2);
}

Night.sender:
{
