Idle.sender:
{
  // simulate a connected data logger device
  state.battery = state.battery - 1
  var now = dayjs().format()

  var message = {
    timestamp: now,
    battery: state.battery,
    cpu_usage: cpu(),
    ram_usage: memory(),
    cpu_temperature: 50 + chance.integer({ min: -10, max: 30 }),
  }

  if (state.battery < 5) {
    next('Charging')
  }
  else {
    if (chance.bool({ likelihood: 5 })) {
      next("Active");
    } else {
      next("Idle");
    }
  }

  return JSON.stringify(message, null, 2)
}

Active.sender:
{
  // simulate a connected data logger device
  state.battery = state.battery - 2;
  var now = dayjs().format();

  var message = {
    timestamp: now,
    battery: state.battery,
    cpu_usage: cpu(),
    ram_usage: memory(),
    cpu_temperature: 70 + chance.integer({ min: -10, max: 30 }),
    air_temperature: weather.temperature({ location: "Switzerland", unit: "c", }),
    humidity: weather.humidity({ location: "Switzerland" }),
  };

  if (state.battery < 5) {
    next("Charging");
  }

  if (chance.bool({ likelihood: 5 })) {
    next("Idle");
  } else {
    next("Active");
  }

  return JSON.stringify(message, null, 2);
}
