Init.sender:
{
  state.smart_city = glob.get('Enable_Smart_City')

  state.status = "Idle"

  state.id = chance.integer({ min: 1000, max: 9999 });
  state.license = 'ZH ' + chance.string({ length: 6, pool: '0123456789' });

  state.make = "Porsche";
  state.model = "Taycan";

  state.latitude = chance.latitude({ min: 12.8, max: 13.1});
  state.longitude = chance.latitude({ min: 12.8, max: 13.1 });

  state.battery_threshold = chance.integer({ min: 1, max: 30 });
  state.battery_capacity = 84;
  state.battery_level = 80;
  state.max_range = 39000;  //390000
  state.available_range = 39000;
  state.avg_usage = 20;
  state.connector_type = "CCS";

  state.dest_latitude = chance.latitude({ min: 12.8, max: 13.1 });
  state.dest_longitude = chance.latitude({ min: 12.8, max: 13.1 });
  state.charging = false;

  state.charger = 0;
  state.charge_lat = chance.latitude({ min: 12.8, max: 13.1 });
  state.charge_long = chance.latitude({ min: 12.8, max: 13.1 });

  state.distance = 0;

  state.start_point = state.latitude + "," + state.longitude;
  next('Driving_Dest')
}

Running.sender:
{
  function setCoordinates() {
    state.dest_latitude = chance.latitude({ min: 47.4, max: 47.8 });
    state.dest_longitude = chance.longitude({ min: 8.4, max: 8.8 });
  }

  function setPosition(latitude, longitude) {
    state.latitude = latitude;
    state.longitude = longitude;

    payload.latitude = latitude;
    payload.longitude = longitude;
  }

  function getDistance(lat_1, long_1, lat_2, long_2) {
    var change = geolib.getDistance(
      { latitude: lat_1, longitude: long_1 },
      { latitude: lat_2, longitude: long_2 }
    );
    state.distance = state.distance + change;
    state.available_range = state.available_range - change;
  }

  function batteryPercentage() {
    state.battery_level = Math.ceil((state.available_range / state.max_range) * 100);
  }

  var payload = {};

  payload.id = state.id;
  payload.license = state.license;
  payload.make = state.make;
  payload.model = state.model;
  payload.battery_capacity = state.battery_capacity;
  payload.battery_level = state.battery_level;
  payload.available_range = state.available_range;
  payload.avg_usage = state.avg_usage;
  payload.connector_type = state.connector_type;
  payload.dest_latitude = state.dest_latitude;
  payload.dest_longitude = state.dest_longitude;
  payload.charging = state.charging;

  if (state.status == "driving_dest") {
    state.charging = false;

    end_point = state.dest_latitude + "," + state.dest_longitude;
    state.location = drive({ start: state.start_point, end: end_point });

    var myglob = glob.get("vehicle_" + state.id + "_cmd");

    if (myglob.isSet !== false) {
      state.charger = myglob.charger;
      state.charge_lat = myglob.latitude;
      state.charge_long = myglob.longitude;

      state.current_location = "" + state.location.latitude + "," + state.location.longitude;
      state.status = "driving_charger";
    }

    getDistance(state.latitude, state.longitude, state.location.latitude, state.location.longitude);
    batteryPercentage();
    setPosition(state.location.latitude, state.location.longitude);

    if (state.location.finished) {
      console.log("We have reached the current destination, but the journey is not yet over.")
      state.start_point = state.latitude + "," + state.longitude;
      setCoordinates();
      state.status = "driving_dest"
    }
  }

  else if (state.status == "driving_charger") {
    charger_location = "" + state.charge_lat + "," + state.charge_long;
    state.location = drive({ start: state.current_location, end: charger_location });

    if (state.location.finished) {
      glob.set("vehicle_" + state.id + "_cmd", { isSet: false });
      state.status = "charger";
    }

    getDistance(state.latitude, state.longitude, state.location.latitude, state.location.longitude);
    batteryPercentage();
    setPosition(state.location.latitude, state.location.longitude);

  }

  else if (state.status == "charger") {
    state.charging = true;
    //state.location.finished=false;

    state.available_range = state.available_range + (state.max_range / 25);
    batteryPercentage();

    if (state.available_range >= state.max_range) {
      state.start_point = "" + state.charge_lat + "," + state.charge_long;
      state.status = "driving_dest";
    }

    state.latitude = state.charge_lat;
    state.longitude = state.charge_long;

  }

  var map_payload = payload;

  map_payload.lat = payload.latitude;
  map_payload.lng = payload.longitude;
  map_payload.client = payload.id
  map_payload.car_status = state.status;

  // var codesandbox = glob.get('codesandbox');


  // rest.post({url: codesandbox, json: map_payload});

  return JSON.stringify(map_payload, null, 2);
}

