Init.sender:
{
  //initialize some default values for your home

  state.address = chance.address();
  // state.gps = location({ address: 'San Francisco, USA', accuracy: 500 });
  state.alarmEnabled = false;
  state.alarmDetected = false;
  state.rooms = {
    living: { name: "Living Room", lights: true, occupancy: true, aircondition: true, temperature: 55, alarm: false },
    bed: { id: 2, name: "Bed Room", lights: false, occupancy: false, aircondition: false, temperature: 60, alarm: false },
    kitchen: { id: 3, name: "Kitchen", lights: false, occupancy: false, aircondition: false, temperature: 58, alarm: false }
  };

  state.currentPowerConsumption = 0; // watt

  next('day')
}

Day.sender:
{
  let currentHour = dayjs().hour()

  if (currentHour > 7 && currentHour < 20) //normal day
    {
      // Lights and airconditioning will mostly be dependent upon occupancy. 
      state.rooms.kitchen.occupancy = chance.bool({ likelihood: 30 });
      state.rooms.kitchen.lights = chance.bool({ likelihood: 30 });
      state.rooms.kitchen.aircondition = state.rooms.kitchen.occupancy ? true : chance.bool({ likelihood: 30 });
      state.rooms.kitchen.temperature = chance.integer({ min: 45, max: 55 });
      state.rooms.kitchen.alarm = chance.bool({ likelihood: 30 });

      state.rooms.living.occupancy = chance.bool({ likelihood: 30 });
      state.rooms.living.lights = state.rooms.living.occupancy ? true : chance.bool({ likelihood: 30 });
      state.rooms.living.aircondition = state.rooms.living.occupancy ? true : chance.bool({ likelihood: 30 });
      state.rooms.living.temperature = chance.integer({ min: 45, max: 55 });
      state.rooms.living.alarm = chance.bool({ likelihood: 30 });

      state.rooms.bed.lights = chance.bool({ likelihood: 30 });
      state.rooms.bed.occupancy = chance.bool({ likelihood: 30 });
      state.rooms.bed.aircondition = state.rooms.bed.occupancy ? true : chance.bool({ likelihood: 30 });;
      state.rooms.bed.temperature = chance.integer({ min: 45, max: 55 });
      state.rooms.bed.alarm = false;
  }  

  else {
    next('Night');
  }
  // The device model state could be transitioned by calling next()
  // To transition to a specific state pass state name as argument e.g.

}

Night.sender:
{
  let currentHour = dayjs().hour()

  if (currentHour < 7 || currentHour > 20) {
    state.rooms.kitchen.lights = false;
    state.rooms.kitchen.occupancy = false;
    state.rooms.kitchen.aircondition = false;
    state.rooms.kitchen.temperature = 45;
    state.rooms.kitchen.alarm = false;

    state.rooms.living.lights = false;
    state.rooms.living.occupancy = false;
    state.rooms.living.aircondition = false;
    state.rooms.living.temperature = 48;
    state.rooms.living.alarm = false;

    state.rooms.bed.lights = false;
    state.rooms.bed.occupancy = true;
    state.rooms.bed.aircondition = true;
    state.rooms.bed.temperature = 50;
    state.rooms.bed.alarm = false;
  } 

  else {
    next('Day');
  }
  // The device model state could be transitioned by calling next()
  // To transition to a specific state pass state name as argument e.g.

}
