Init.sender:
{
  //initialize some default values for your home

  state.address = chance.address();
  // state.gps = location({ address: 'San Francisco, USA', accuracy: 500 });
  state.alarmEnabled = false;
  state.alarmDetected = false;
  state.rooms = {
    living: { name: "Living Room", lights: true, occupancy: true, aircondition: true, temperature: 55, alarm: false },
    bed: { id: 2, name: "Bed Room", lights: false, occupancy: false, aircondition: false, temperature: 60, alarm: false },
    kitchen: { id: 3, name: "Kitchen", lights: false, occupancy: false, aircondition: false, temperature: 58, alarm: false }
  };

  state.currentPowerConsumption = 0; // watt

  next('day')
}

Day.sender:
{
  let currentHour = dayjs().hour()

  if (currentHour > 7 && currentHour < 20) //normal day
    {
      // Lights and airconditioning will mostly be dependent upon occupancy. 
      state.rooms.kitchen.occupancy = chance.bool({ likelihood: 30 });
      state.rooms.kitchen.lights = state.rooms.kitchen.occupancy ? chance.bool({ likelihood: 5 }) : false;
      state.rooms.kitchen.aircondition = state.rooms.kitchen.occupancy ? chance.bool({ likelihood: 50 }) : false;
      state.rooms.kitchen.temperature = state.rooms.kitchen.aircondition ? chance.integer({ min: 15, max: 30 }): chance.integer({ min: 45, max: 55 });
      state.rooms.kitchen.alarm = chance.bool({ likelihood: 30 });

      state.rooms.living.occupancy = chance.bool({ likelihood: 40 });
      state.rooms.living.lights = state.rooms.living.occupancy ? chance.bool({ likelihood: 5 }) : false;
      state.rooms.living.aircondition = state.rooms.living.occupancy ? chance.bool({ likelihood: 50 }) : false;
      state.rooms.living.temperature = state.rooms.living.aircondition ? chance.integer({ min: 15, max: 30 }) : chance.integer({ min: 45, max: 55 });
      state.rooms.living.alarm = chance.bool({ likelihood: 30 });

      state.rooms.bed.occupancy = chance.bool({ likelihood: 30 });
      state.rooms.bed.lights = state.rooms.bed.occupancy ? chance.bool({ likelihood: 5 }) : false;
      state.rooms.bed.aircondition = state.rooms.bed.occupancy ? chance.bool({ likelihood: 5 }) : false;
      state.rooms.bed.temperature = state.rooms.bed.aircondition ? chance.integer({ min: 15, max: 30 }) : chance.integer({ min: 45, max: 55 });
      state.rooms.bed.alarm = false;
  }  

  else {
    next('Night');
  }

}

Night.sender:
{
  let currentHour = dayjs().hour()

  if (currentHour < 7 || currentHour > 20) {

    state.rooms.kitchen.occupancy = chance.bool({ likelihood: 5 });
    state.rooms.kitchen.lights = state.rooms.kitchen.occupancy ? chance.bool({ likelihood: 50 }) : false;
    state.rooms.kitchen.aircondition = state.rooms.kitchen.occupancy ? chance.bool({ likelihood: 10 }) : false;
    state.rooms.kitchen.temperature = state.rooms.kitchen.aircondition ? chance.integer({ min: 15, max: 30 }) : chance.integer({ min: 45, max: 55 });
    state.rooms.kitchen.alarm = chance.bool({ likelihood: 30 });

    state.rooms.living.occupancy = chance.bool({ likelihood: 20 });
    state.rooms.living.lights = state.rooms.living.occupancy ? chance.bool({ likelihood: 50 }) : false;
    state.rooms.living.aircondition = state.rooms.living.occupancy ? chance.bool({ likelihood: 50 }) : false;
    state.rooms.living.temperature = state.rooms.living.aircondition ? chance.integer({ min: 15, max: 30 }) : chance.integer({ min: 45, max: 55 });
    state.rooms.living.alarm = chance.bool({ likelihood: 30 });

    state.rooms.bed.occupancy = chance.bool({ likelihood: 75 });
    state.rooms.bed.lights = state.rooms.bed.occupancy ? chance.bool({ likelihood: 50 }) : false;
    state.rooms.bed.aircondition = state.rooms.bed.occupancy ? chance.bool({ likelihood: 50 }) : false;
    state.rooms.bed.temperature = state.rooms.bed.aircondition ? chance.integer({ min: 15, max: 30 }) : chance.integer({ min: 45, max: 55 });
    state.rooms.bed.alarm = false;
  } 

  else {
    next('Day');
  }

}
