Idle.sender:
{
  var spin_speed
  var now = dayjs().format()

  if (chance.bool({ likelihood: 10 })) {
    state.current_program = chance.integer({ min: 0, max: 4 })
    if (chance.integer({ min: 0, max: 100 }) > 60) {
      state.status = "delayed_start"
      state.start_time = index() + 5
      state.load = chance.floating({ min: 2, max: 4 })
      state.door = "closed"
      next('Delayed')
    }
    else {
      state.status = "started"
      state.start_time = index()
      state.door = "locked"
      spin_speed = 0
      next('Started')
    }
  }

  var message = {
    timestamp: now,
    status: state.status,
    door_state: state.door,
  }
  return JSON.stringify(message, null, 2);
}

Started.sender:
{
  var spin_speed
  var now = dayjs().format()

  if (index() < state.start_time + state.programs[state.current_program].duration - state.programs[state.current_program].spin_duration)
    spin_speed = chance.floating({ min: 0, max: 60 })
  else if (index() < state.start_time + state.programs[state.current_program].duration)
    spin_speed = state.programs[state.current_program].spin_speed
  else {
    state.status = "idle"
    state.door = "open"
  }

  var message = {
    timestamp: now,
    status: state.status,
    door_state: state.door,
  }
  if (state.status != "idle") {
    message.program = state.programs[state.current_program].name
    message.temperature = state.programs[state.current_program].temperature
    message.duration = state.programs[state.current_program].duration
    message.load = state.load
    message.spin_speed = spin_speed
  }

}

Delayed.sender:
{
  // Please provide your logic here.
  var spin_speed

  if (index() == state.start_time) {
    state.status = "started"
    state.start_time = index()
    state.load = chance.floating({ min: 2, max: 4 })
    state.door = "locked"
    spin_speed = 0
  }
}
