Running.sender:
{
  var now = dayjs().format()

  var steps = chance.integer({ min: 0, max: 50 })
  state.step_count = state.step_count + steps

  state.position.lat = state.position.lat + steps * chance.floating({ min: -0.00003, max: 0.00003 })
  state.position.lon = state.position.lon + steps * chance.floating({ min: -0.00003, max: 0.00003 })

  var message = {
    timestamp: now,
    position: state.position,
    step_count: state.step_count,
    body_temperature: 37 + chance.floating({ min: -0.5, max: 0.5 }),
    heart_rate: volatile({ min: 60, max: 120, delta: 2, key: "heartRate", }),
    blood_oxygen: volatile({ min: 97, max: 100, delta: 1, key: "bloodOxygen", }),
  }

  if (chance.bool({ likelihood: 5 })) {
    next('Resting')
  }
  else {
    if (chance.bool({ likelihood: 90 })) {
      next('Running')
    }
    else {
      next('Walking')
    }
  }

  metric.add('Heart_Rate_'+client(), message.heart_rate)
  metric.add('Steps_' + client(), state.step_count)

  return JSON.stringify(message, null, 2);
}

