Init.sender:
{
  var API_Key = glob.get('API_Key')
  console.log(API_Key)

  state.smart_city = false

  state.r_latitude = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
  state.r_longitude = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });

  var url = 'https://roads.googleapis.com/v1/nearestRoads?points=' + state.r_latitude + "," + state.r_longitude + '&key=' + API_Key

  var res = rest.get(url)
  console.log(res)

  state.location = res.snappedPoints[0].location.latitude + ',' + res.snappedPoints[0].location.longitude

  state.status = 'off'
  state.energy_produced = 0
  state.energy_consumed = 0

  next('Day')
}

Day.sender:
{
  let currentHour = dayjs().hour()

  if (currentHour > 7 && currentHour < 20) {

    state.status = 'off'
    var output = chance.integer({ min: 1, max: 6 })
    state.energy_produced += output

    var message = {
      device: "street_light" + client(),
      location:state.location,
      status: state.status,
      energy_produced: state.energy_produced,
      energy_consumed: state.energy_consumed,
    }

    if (state.smart_city == true) {
      mailbox.post('trash_can_' + client(), message)
    }
    else {
      return JSON.stringify(message, null, 2);
    }
  }
  else {
    next('Night')
  }
}

Night.sender:
{
  let currentHour = dayjs().hour()

  if (currentHour < 7 || currentHour > 20) {

    state.status = 'on'
    var consumption = chance.integer({ min: 1, max: 6 })
    state.energy_consumed += consumption

    var message = {
      device: "street_light" + client(),
      location: state.location,
      status: state.status,
      energy_produced: state.energy_produced,
      energy_consumed: state.energy_consumed,
    }

    if (state.smart_city == true) {
      mailbox.post('trash_can_' + client(), message)
    }
    else {
      return JSON.stringify(message, null, 2);
    }
  }
  else {
    next('Night')
  }
}
