Init.sender:
{


  var API_Key = glob.get('API_Key')

  state.smart_city = false

  state.r_latitude = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
  state.r_longitude = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });

  var url = 'https://roads.googleapis.com/v1/nearestRoads?points=' + state.r_latitude + "," + state.r_longitude + '&key=' + API_Key

  var res = rest.get(url)

  state.location = res.snappedPoints[0].location.latitude + ',' + res.snappedPoints[0].location.longitude

  state.current_light = chance.pickone(['green', 'yellow', 'red']);

  next('Active')
}

Active.sender:
{
  if (state.timer_active == true) {
    state.current_timer -= 10
    if (state.current_timer < 0) {
      state.timer_active = false
      if (state.current_light == 'green') {
        state.current_light == 'yellow'
      }
      else if (state.current_light == 'yellow') {
        state.current_light == 'red'
      }
      else if (state.current_light == 'red') {
        state.current_light == 'green'
      }
    }
  }

  else {
    if (state.current_light != 'yellow') {
      state.current_timer = chance.integer({ min: 0, max: 20 })
      state.timer_active = true
    }
    else {
      state.current_timer = chance.integer({ min: 0, max: 200 })
      state.timer_active = true
    }
  }

  var message = {
    device: "traffic_light" + client(),
    location: state.location,
    current_state: state.current_light,
    current_wait: state.state.current_timer,
    status: "active",
  }

  if (state.smart_city == true) {
    mailbox.post('trash_can_' + client(), message)
  }
  else {
    return JSON.stringify(message, null, 2);
  }
}
