    next('Night')
  }
}

Cleaning.sender:
{
  let currentHour = dayjs().hour()

  if (state.cleaning_trash_can = true) {
    console.log("Emptying trash can")
    if (state.fill > 20) {
      state.fill -= 20
    }
    else {
      state.fill = 0
      state.fill_alert = false
      state.cleaning_trash_can = false

      if (currentHour > 7 && currentHour < 20) {
        next('Day')
      }
      else {
        next('Night')
      }
    }

    var message = {
      device: "trash_can",
      id: client(),
      latitude: state.latitude,
      longitude: state.longitude,
      fill: state.fill,
      alert: state.fill_alert,
      status: "cleaning",
    }

    if (state.smart_city == true) {
      mailbox.post('trash_can_' + client(), message)
      return JSON.stringify(message, null, 2);

    }
    else {
      return JSON.stringify(message, null, 2);
    }
  }
}
