Init.sender:
{
  var API_Key = glob.get('API_Key')

  state.smart_city = glob.get('Enable_Smart_City')

  state.r_latitude = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
  state.r_longitude = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });

  var url = 'https://roads.googleapis.com/v1/nearestRoads?points=' + state.r_latitude + "," + state.r_longitude + '&key=' + API_Key

  var res = rest.get(url)

  state.latitude = res.snappedPoints[0].location.latitude
  state.longitude = res.snappedPoints[0].location.longitude
  state.fill = 0
  state.fill_alert = false

  next('Day')
}

Day.sender:
{
  let currentHour = dayjs().hour();

  // if ((state.fill_alert = true)) {
  //   if (chance.bool({ likelihood: 30 })) {
  //     state.cleaning_trash_can = true;

  //     next("Cleaning");
  //   }
  // }
  if (state.smart_city == true) {
    for (let i = 0; i < 50; ++i) {
      let count = mailbox.count("trash_can_" + client() + "_rx");
      if (count > 0) {
        let mailbox_message = mailbox.pop("trash_can_" + client() + "_rx");
        let garbage_truck = mailbox_message;

        if (garbage_truck.device == "garbage_truck") next("Cleaning");
      }
    }
  }
  console.log("Currently it's: " + currentHour)

  if (currentHour => 7 && currentHour < 20) {
      var trash = chance.integer({ min: 1, max: 5 });
      state.fill += trash;


    if (state.fill >= 80) {
      state.fill_alert = true;
      mailbox.post("trash_can_" + client() + "_tx", message);
    }

    var message = {
      device: "trash_can",
      id: client(),
      latitude: state.latitude,
      longitude: state.longitude,
      fill: state.fill,
      alert: state.fill_alert,
      status: "active",
    };

    if (state.smart_city == true) {
      mailbox.post("trash_can_" + client(), message);
    }
    return JSON.stringify(message, null, 2);
  } else {
    next("Night");
  }
}

