Maintenance.sender:
if (state.maintenance_due) {

  var now = dayjs().format()
  state.maintenance_due = false

  var message = {
    timestamp: now,
    status: "Under maintenance"
  }

  if (--state.maintenance_cycles <= 0) {
    state.maintenance_cycles = 5
    state.maintenance_due = false

    next('On_Ride')
  }

  return JSON.stringify(message, null, 2);
}
