Init.sender:
{
  state.API_Key = glob.get('API_Key')
  state.smart_city = glob.get('Enable_Smart_City')

  state.r_latitude = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
  state.r_longitude = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });

  var url = 'https://roads.googleapis.com/v1/nearestRoads?points=' + state.r_latitude + "," + state.r_longitude + '&key=' + API_Key

  var res = rest.get(url)

  state.latitude = state.base_latitude = res.snappedPoints[0].location.latitude
  state.longitude = state.base_longitude = res.snappedPoints[0].location.longitude

  state.dest_latitude = state.latitude
  state.dest_longitude = state.longitude

  next('Idle')
}

Idle.sender:
{
  var message = {
    device: "garbage_truck",
    id: client(),
    dest_latitude: state.dest_latitude,
    dest_longitude: state.dest_longitude,
    current_latitude: state.latitude,
    current_longitude: state.longitude,
    status: "Idle",
  };

  if (state.smart_city == true) {
    for (let i = 0; i < 50; ++i) {
      let count = mailbox.count("ev_car_" + client() + "_rx");
      if (count > 0) {
        let mailbox_message = mailbox.pop("ev_car_" + client() + "_rx");
        let smart_parking = mailbox_message;
        state.search_parking = false;
        state.parking_latitude = state.dest_latitude = smart_parking.latitude;
        state.parking_longitude = state.dest_longitude =
          smart_parking.longitude;
        console.log("Parking found driving to parking spot");

        state.current_location = "" + state.latitude + "," + state.longitude;
        state.parking_location =
          "" + state.parking_latitude + "," + state.parking_longitude;

        next("Driving_Parking");
      }
    }
  }

  state.end_point = state.dest_latitude + "," + state.dest_longitude;

  state.location = drive({
    start: state.start_point,
    end: state.end_point,
    key: state.API_Key,
  });
  console.log(state.location);

  var change = geolib.getDistance(
    { latitude: state.latitude, longitude: state.longitude },
    { latitude: state.location.latitude, longitude: state.location.longitude }
  );
  state.distance = state.distance + change;
  state.available_range = state.available_range - change;

  state.battery_level = Math.ceil(
    (state.available_range / state.max_range) * 100
  );
  state.latitude = state.location.latitude;
  state.longitude = state.location.longitude;

  if (state.location.finished) {
    console.log(
      "We have reached the current destination, Going to the nearest parking."
    );
    state.search_parking = true;
  }

  if (
    state.search_parking == true ||
    state.battery_level < state.battery_threshold
  ) {
    console.log("Currently searching for the nearest parking.");

    if (state.smart_city == true) {
      mailbox.post("ev_car_" + client() + "_parking", message);
    } else {
      state.parking_latitude = state.dest_latitude = chance.floating({
        min: 12.8,
        max: 13.1,
        fixed: 6,
      });
      state.parking_longitude = state.dest_longitude = chance.floating({
        min: 77.5,
        max: 77.8,
        fixed: 6,
      });
      console.log("Parking found driving to parking spot");

      state.current_location = "" + state.latitude + "," + state.longitude;
      state.parking_location =
        "" + state.parking_latitude + "," + state.parking_longitude;

      next("Driving_Parking");
    }
  }

  if (state.smart_city == true) {
    mailbox.post("ev_car_" + client() + "_tx", message);
  }
  return JSON.stringify(message, null, 2);
}

Driving_Parking.sender:
{
  var message = {
    device: "ev_car",
    id: client(),
    license: state.license,
    make: state.make,
    model: state.model,
    battery_capacity: state.battery_capacity,
    battery_level: state.battery_level,
    available_range: state.available_range,
    connector_type: state.connector_type,
    dest_latitude: state.dest_latitude,
    dest_longitude: state.dest_longitude,
    current_latitude: state.latitude,
    current_longitude: state.longitude,
    charging: state.charging,
    status: "Driving to Parking",
  };

  state.location = drive({
    start: state.current_location,
    end: state.parking_location,
    key: state.API_Key,
  });

  var change = geolib.getDistance(
    { latitude: state.latitude, longitude: state.longitude },
    { latitude: state.location.latitude, longitude: state.location.longitude }
  );
  state.distance = state.distance + change;
  state.available_range = state.available_range - change;

  state.battery_level = Math.ceil(
    (state.available_range / state.max_range) * 100
  );

  state.latitude = state.location.latitude;
  state.longitude = state.location.longitude;

  if (state.location.finished) {
    console.log("We have reached the parking spot.");
    state.start_point = state.latitude + "," + state.longitude;

    next("Parking");
  }

  if (state.smart_city == true) {
    mailbox.post("ev_car_" + client() + "_tx", message);
  }

  return JSON.stringify(message, null, 2);
}

Parking.sender:
{
  var message = {
    device: "ev_car",
    id: client(),
    license: state.license,
    make: state.make,
    model: state.model,
    battery_capacity: state.battery_capacity,
    battery_level: state.battery_level,
    available_range: state.available_range,
    connector_type: state.connector_type,
    dest_latitude: state.dest_latitude,
    dest_longitude: state.dest_longitude,
    current_latitude: state.latitude,
    current_longitude: state.longitude,
    charging: state.charging,
    status: "Parked",
  };

  state.charging = true;

  state.available_range = state.available_range + state.max_range / 25;

  state.battery_level = Math.ceil(
    (state.available_range / state.max_range) * 100
  );

  console.log("Currently parked; Battery charging");

  if (chance.bool({ likelihood: 30 }) || state.available_range >= state.max_range) {
    state.start_point = state.parking_location;
    state.dest_latitude = chance.floating({ min: 12.8, max: 13.1, fixed: 6 });
    state.dest_longitude = chance.floating({ min: 77.5, max: 77.8, fixed: 6 });
    next("Driving_Dest");
  } else {
    next("Parking");
  }

  if (state.smart_city == true) {
    mailbox.post("ev_car_" + client() + "_tx", message);
  }

  return JSON.stringify(message, null, 2);
}
