  var freezer_door_opened

  if (chance.floating({ min: 0, max: 1 }) < 0.2) {
    fridge_door_opened = false
    freezer_door_opened = true

    if (state.freezer_temperature > state.target_freezer_temperature) {
      state.freezer_temperature = state.freezer_temperature + (state.target_freezer_temperature - state.freezer_temperature) / 2
    }
    if (state.fridge_temperature > state.target_fridge_temperature){
      state.fridge_temperature = state.fridge_temperature + (state.target_fridge_temperature - state.fridge_temperature) / 2
    }
  }
  else {
    if (chance.floating({ min: 0, max: 1 }) < 0.5) {
      freezer_door_opened = true
      state.freezer_temperature = state.freezer_temperature + 5
    }
    else {
      freezer_door_opened = false
      if (state.freezer_temperature > state.target_freezer_temperature)
        state.freezer_temperature = state.freezer_temperature + (state.target_freezer_temperature - state.freezer_temperature) / 2
    }
  }

  var message = {
    fridge_temperature: state.fridge_temperature,
    target_fridge_temperature: state.target_fridge_temperature,
    freezer_temperature: state.freezer_temperature,
    target_freezer_temperature: state.target_freezer_temperature,
    fridge_door_opened: fridge_door_opened,
    freezer_door_opened: freezer_door_opened,
  }
  return JSON.stringify(message, null, 2);
}

